import React, { useState } from 'react';
import { Button, Drawer } from 'antd';
import { Avatar, Layout, Input } from "antd";
import Nav from "../../components/Nav";

const { Header, Footer, Sider, Content } = Layout;
const { Search } = Input;

function DefaultLayout({ children }) {
  const onSearch = (value) => console.log(value);

  const [open, setOpen] = useState(false);
  const showDrawer = () => {
    setOpen(true);
  };
  const onClose = () => {
    setOpen(false);
  };
  return (
    <Layout>
      <Header style={{ backgroundColor: "#fff", display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Avatar></Avatar>
        <Search
          style={{
            width: 200,
          }}
          placeholder="input search text"
          onSearch={onSearch}
          enterButton
        />
        <Nav />
        <>
      <Button type="primary" onClick={showDrawer}>
        Open
      </Button>
      <Drawer title="Basic Drawer" closeIcon="right" placement="left" onClose={onClose} open={open}>
        <p>Some contents...</p>
        <p>Some contents...</p>
        <p>Some contents...</p>
      </Drawer>
    </>
      </Header>
      <Layout>
        <Sider>Sider</Sider>
        <Content>{children}</Content>
        <Sider>Sider</Sider>
      </Layout>
      <Footer>Footer</Footer>
    </Layout>
  );
}

export default DefaultLayout;
