import { Avatar, Button, Image } from "antd";
import React from "react";
import { RiEarthFill } from "react-icons/ri";
import {BsThreeDots} from "react-icons/bs"

export default function Post() {
  return (
    <div className="wrapper-post">
      <div style={{ display: "flex", alignItems: "center", justifyContent: 'space-between' }}>
        <div>
          <Avatar
            size="large"
            src={
              <Image src="https://scontent.fvca2-1.fna.fbcdn.net/v/t39.30808-6/316286227_3395030304070723_4467159302786940404_n.jpg?stp=dst-jpg_p843x403&_nc_cat=108&ccb=1-7&_nc_sid=5cd70e&_nc_ohc=GVJK_if5ZPQAX-dmuDI&tn=oo4Ib4hG_5wIMs4B&_nc_ht=scontent.fvca2-1.fna&oh=00_AfCH_cgcy1o-tq8xYNsOwPBVgYCayoGjL_EtMl-1r1R7uA&oe=63865CD8" />
            }
          />
          <div
            style={{ display: "flex", flexDirection: "column", marginLeft: 10 }}
          >
            <span style={{ fontSize: 16, fontWeight: 500 }}>Lương Khoa</span>
            <span style={{ display: "flex", alignItems: "center" }}>
              <span>3 ngày</span>
              <RiEarthFill style={{ marginLeft: 10 }} />
            </span>
          </div>
        </div>
        <Button type="text"><BsThreeDots /></Button>
      </div>
    </div>
  );
}
