import { Row, Col, Avatar, Image, Button } from "antd";
import React from "react";
import Story from "../../components/Story";
import { AiOutlineVideoCameraAdd } from "react-icons/ai";
import { MdOutlineCollections, MdInsertEmoticon } from "react-icons/md";
export default function Home() {
  return (
    <div style={{ backgroundColor: "transparent", height: 1000 }}>
      <Row>
        <Col span={24}>
          <Story />
        </Col>
      </Row>

      <Row
        style={{
          margin: "20px 0",
          backgroundColor: "white",
          padding: 20,
          borderRadius: 8,
          boxShadow:
            "rgba(50, 50, 93, 0.25) 0px 2px 5px -1px, rgba(0, 0, 0, 0.3) 0px 1px 3px -1px",
        }}
      >
        <Col span={24}>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Avatar
              style={{ width: 40, height: 40, marginRight: 10 }}
              src={
                <Image
                  src="https://joeschmoe.io/api/v1/random"
                  style={{ width: 40 }}
                  preview={false}
                />
              }
            />
            <Button
              className="btn-add-post"
              type="text"
              style={{
                backgroundColor: "#F0F2F5",
                flex: 1,
                borderRadius: 40,
                textAlign: "left",
                fontSize: 16,
                display: "flex",
                alignItems: "center",
                padding: "20px",
              }}
            >
              <span style={{ opacity: 0.6 }}>
                Khoa ơi! bạn đang nghĩ gì thế?
              </span>
            </Button>
          </div>
          <div style={{ display: "flex" , justifyContent: 'space-between'}}>
            <Button
              type="text"
              style={{
                fontSize: 16,
                display: "flex",
                alignItems: "center",
                padding: "20px",
              }}
            >
              <AiOutlineVideoCameraAdd style={{marginRight: 10, color: 'red', fontSize: 25}} />
              <span>Video trực tiếp</span>
            </Button>
            <Button
              type="text"
              style={{
                fontSize: 16,
                display: "flex",
                alignItems: "center",
                padding: "20px",
              }}
            >
              <MdOutlineCollections style={{marginRight: 10, color: 'green', fontSize: 25}} />
              <span>Ảnh/video</span>
            </Button>
            <Button
              type="text"
              style={{
                fontSize: 16,
                display: "flex",
                alignItems: "center",
                padding: "20px",
              }}
            >
              <MdInsertEmoticon style={{marginRight: 10, color: 'yellow', fontSize: 25}} />
              <span>Feeling/activity</span>
            </Button>
          </div>
        </Col>
      </Row>
    </div>
  );
}
