import { Avatar, Button, Image } from "antd";
import React from "react";
import { RiEarthFill } from "react-icons/ri";
import { BsThreeDots } from "react-icons/bs";
import { AiTwotoneLike, AiOutlineLike } from "react-icons/ai";
import { BiComment } from "react-icons/bi";
import { RiShareForwardLine } from "react-icons/ri";

export default function Post({imgAuthor, content, img, timeUp, timeUpNano, author}) {
  const fireBaseTime = new Date(
    timeUp * 1000 + timeUpNano / 1000000,
  );

  const date = fireBaseTime.toDateString();
  const atTime = fireBaseTime.toLocaleTimeString();


  return (
    <div className="wrapper-post" style={{backgroundColor: 'white'}}>
      <div
        style={{
          padding: 10,
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <Avatar
            size="large"
            src={
              <Image src={imgAuthor} />
            }
          />
          <div
            style={{ display: "flex", flexDirection: "column", marginLeft: 10 }}
          >
            <span style={{ fontSize: 16, fontWeight: 500 }}>{author}</span>
            <span style={{ display: "flex", alignItems: "center" }}>
              <span>{`${date} ${atTime}`}</span>
              <RiEarthFill style={{ marginLeft: 10 }} />
            </span>
          </div>
        </div>
        <Button type="text" style={{ fontSize: 20, borderRadius: 9999 }}>
          <BsThreeDots />
        </Button>
      </div>
      <div style={{ padding: 10 }}>
        <p>
         {content}
        </p>
      </div>
      <div style={{ height: 282 }}>
        <Image
          preview={false}
          height="100%"
          width="100%"
          src={img}
        />
      </div>
    <div style={{padding: '0 20px 20px 20px'}}>
        <div
          style={{
            display: "flex",
            padding: "5px 0",
            justifyContent: "space-between",
            borderBottom: "1px solid #BFBFBF",
          }}
        >
          <span style={{ display: "flex", alignItems: "center" }}>
            <span
              style={{
                borderRadius: 9999,
                backgroundColor: "#149DF5",
                padding: "2px 3px",
                color: "white",
                marginRight: 5,
                fontSize: 12,
              }}
            >
              <AiTwotoneLike />
            </span>
            5
          </span>
          <span>3 bình luận</span>
        </div>
        <div
          style={{
            display: "flex",
            padding: "2px 0",
            justifyContent: "space-between",
            borderBottom: "1px solid #BFBFBF",
          }}
        >
          <Button
            type="text"
            style={{
              display: "flex",
              alignItems: "center",
              fontSize: 16,
              fontWeight: 500,
              color: "#606266",
            }}
          >
            <AiOutlineLike style={{ marginRight: 5 }} /> Thích
          </Button>
          <Button
            type="text"
            style={{
              display: "flex",
              alignItems: "center",
              fontSize: 16,
              fontWeight: 500,
              color: "#606266",
            }}
          >
            <BiComment style={{ marginRight: 5 }} /> Bình luận
          </Button>
          <Button
            type="text"
            style={{
              display: "flex",
              alignItems: "center",
              fontSize: 16,
              fontWeight: 500,
              color: "#606266",
            }}
          >
            <RiShareForwardLine style={{ marginRight: 5 }} /> Thích
          </Button>
        </div>
    </div>
    </div>
  );
}
