import React, { useState } from "react";
import { Button, Drawer, Image } from "antd";
import {
  Avatar,
  Layout,
  Input,
  Menu,
  AutoComplete,
  Modal,
  Tooltip,
} from "antd";
import Nav from "../../components/Nav";
import { AiOutlineMenu } from "react-icons/ai";
import { UserOutlined } from "@ant-design/icons";
import {
  AppstoreOutlined,
  MailOutlined,
  SettingOutlined,
} from "@ant-design/icons";
import images from "../../assets/img";
import { Link } from "react-router-dom";
import {
  LoginOutlined,
  UserAddOutlined,
  InfoCircleOutlined,
} from "@ant-design/icons";
import { Cascader, Checkbox, Col, Form, InputNumber, Row, Select } from "antd";
const { Option } = Select;

const { Header, Footer, Sider, Content } = Layout;

function getItem(label, key, icon, children, type) {
  return {
    key,
    icon,
    children,
    label,
    type,
  };
}

const items = [
  getItem("Navigation One", "sub1", <MailOutlined />, [
    getItem("Option 1", "1"),
    getItem("Option 2", "2"),
    getItem("Option 3", "3"),
    getItem("Option 4", "4"),
  ]),
  getItem("Navigation Two", "sub2", <AppstoreOutlined />, [
    getItem("Option 5", "5"),
    getItem("Option 6", "6"),
    getItem("Submenu", "sub3", null, [
      getItem("Option 7", "7"),
      getItem("Option 8", "8"),
    ]),
  ]),
  getItem("Navigation Three", "sub4", <SettingOutlined />, [
    getItem("Option 9", "9"),
    getItem("Option 10", "10"),
    getItem("Option 11", "11"),
    getItem("Option 12", "12"),
  ]),
];

// submenu keys of first level
const rootSubmenuKeys = ["sub1", "sub2", "sub4"];

function DefaultLayout({ children }) {
  const [open, setOpen] = useState(false);
  const showDrawer = () => {
    setOpen(true);
  };
  const onClose = () => {
    setOpen(false);
  };

  const [openKeys, setOpenKeys] = useState(["sub1"]);
  const onOpenChange = (keys) => {
    const latestOpenKey = keys.find((key) => openKeys.indexOf(key) === -1);
    if (rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
      setOpenKeys(keys);
    } else {
      setOpenKeys(latestOpenKey ? [latestOpenKey] : []);
    }
  };

  const options = [
    { value: "Burns Bay Road" },
    { value: "Downing Street" },
    { value: "Wall Street" },
  ];

  const [loading, setLoading] = useState(false);
  const [openRegister, setOpenRegister] = useState(false);
  const showModal = () => {
    setOpenRegister(true);
  };
  const handleOk = () => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setOpenRegister(false);
    }, 3000);
  };
  const handleCancel = () => {
    setOpenRegister(false);
  };

  return (
    <Layout>
      <Modal
        open={openRegister}
        title="ĐĂNG KÝ"
        onOk={handleOk}
        onCancel={handleCancel}
        footer={[
          <Button key="back" onClick={handleCancel}>
            Trở lại
          </Button>,
          <Button
            key="submit"
            type="primary"
            loading={loading}
            onClick={handleOk}
          >
            Đăng ký
          </Button>,
        ]}
      >
        <div className="modal-register">
          <Form>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
            >
              <Avatar style={{ width: 100, height: 100 }}>
                <Image preview={false} src="" />
              </Avatar>
              <label
                htmlFor="input-avatar-register"
                style={{
                  border: "1px solid #4096FF",
                  padding: "2px 20px",
                  borderRadius: 4,
                  fontWeight: "bold",
                  cursor: "pointer",
                  color: "white",
                  backgroundColor: "#4096FF",
                  margin: "10px 0",
                }}
              >
                Choose avatar
              </label>
              <input
                id="input-avatar-register"
                type="file"
                style={{ margin: "10px 0px", display: "none" }}
              />
            </div>
            <Form.Item
              name="email"
              label="E-mail"
              rules={[
                {
                  type: "email",
                  message: "Không đúng định dạng e-mail!",
                },
                {
                  required: true,
                  message: "Nhập e-mail của bạn!",
                },
              ]}
            >
              <Input />
            </Form.Item>

            <Form.Item
              name="password"
              label="Password"
              rules={[
                {
                  required: true,
                  message: "Nhập mật khẩu của bạn!",
                },
              ]}
              hasFeedback
            >
              <Input.Password />
            </Form.Item>

            <Form.Item
              name="confirm"
              label="Confirm Password"
              dependencies={["password"]}
              hasFeedback
              rules={[
                {
                  required: true,
                  message: "Vui lòng xác nhận mật khẩu!",
                },
                ({ getFieldValue }) => ({
                  validator(_, value) {
                    if (!value || getFieldValue("password") === value) {
                      return Promise.resolve();
                    }
                    return Promise.reject(
                      new Error(
                        "Mật khẩu không trùng khớp!"
                      )
                    );
                  },
                }),
              ]}
            >
              <Input.Password />
            </Form.Item>
            <Form.Item
              name="nickname"
              label="Nickname"
              tooltip="What do you want others to call you?"
              rules={[
                {
                  required: true,
                  message: "Please input your nickname!",
                  whitespace: true,
                },
              ]}
            >
              <Input />
            </Form.Item>
            <Form.Item
              name="gender"
              label="Gender"
              rules={[
                {
                  required: true,
                  message: "Please select gender!",
                },
              ]}
            >
              <Select placeholder="select your gender">
                <Option value="male">Male</Option>
                <Option value="female">Female</Option>
                <Option value="other">Other</Option>
              </Select>
            </Form.Item>
          </Form>
        </div>
      </Modal>

      <Header
        style={{
          backgroundColor: "#fff",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          position: "sticky",
          top: 0,
          zIndex: 1,
          width: "100%",
        }}
      >
        <Button className="btn-menu--mobile" type="text" onClick={showDrawer}>
          <AiOutlineMenu />
        </Button>
        <Link className="logo-header" to="/">
          <Image preview={false} src={images.logo} style={{ height: 40 }} />
        </Link>
        <Nav />
        <AutoComplete
          style={{ width: 300 }}
          options={options}
          children={
            <Input.Search size="middle" placeholder="input here" enterButton />
          }
          filterOption={(inputValue, option) =>
            option.value.toUpperCase().indexOf(inputValue.toUpperCase()) !== -1
          }
        />
        <Drawer
          title="Menu"
          placement="left"
          onClose={onClose}
          open={open}
          style={{ padding: 0 }}
        >
          <Menu
            mode="inline"
            openKeys={openKeys}
            onOpenChange={onOpenChange}
            style={{
              width: "100%",
            }}
            items={items}
          />
        </Drawer>
        {/* <div className="has-auth">
          <Avatar
            style={{ backgroundColor: "#87d068" }}
            icon={<UserOutlined />}
          />
          <Avatar>
            <Image src="" />
          </Avatar>
       </div> */}
        <div className="not-auth">
          <Button
            onClick={showModal}
            icon={<UserAddOutlined />}
            style={{ marginRight: 10 }}
          >
            Register
          </Button>
          <Button type="primary" icon={<LoginOutlined />}>
            Login
          </Button>
        </div>
      </Header>
      <Layout>
        <Sider>Sider</Sider>
        <Content>{children}</Content>
        <Sider>Sider</Sider>
      </Layout>
      <Footer>Footer</Footer>
    </Layout>
  );
}

export default DefaultLayout;
