import { Button, Row, Col, Image } from "antd";
import React, { useState } from "react";
import { BsBookFill, BsCollectionPlayFill } from "react-icons/bs";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function Story() {
  const [tab, setTab] = useState(1);

  return (
    <div style={{ backgroundColor: "white", borderRadius: 8 }}>
      <div style={{ display: "flex", width: "100%", padding: 10 }}>
        <span
          className={tab === 1 && "tab-active-story-border"}
          style={{
            display: "flex",
            width: "100%",
            justifyContent: "center",
          }}
        >
          <Button
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              width: "100%",
              fontSize: 15,
              color: "#606266",
              fontWeight: 500,
            }}
            size="large"
            type="text"
            className={tab === 1 && "tab-active-story"}
            onClick={() => setTab(1)}
          >
            <BsBookFill style={{ marginRight: 10, fontSize: 18 }} />
            Tin
          </Button>
        </span>
        <span
          style={{ display: "flex", width: "100%", justifyContent: "center" }}
          className={tab === 2 && "tab-active-story-border"}
        >
          <Button
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              width: "100%",
              fontSize: 15,
              color: "#606266",
              fontWeight: 500,
            }}
            size="large"
            type="text"
            onClick={() => setTab(2)}
            className={tab === 2 && "tab-active-story"}
          >
            <BsCollectionPlayFill style={{ marginRight: 10, fontSize: 18 }} />
            Reels
          </Button>
        </span>
      </div>
      <Row>
        <Col span={8}>
          <div style={{display: 'flex', flexDirection: 'column',height: 200}}>
            <Image style={{height: '70%', width: '100%'}} preview={false} src="https://scontent.fvca2-1.fna.fbcdn.net/v/t39.30808-6/305994841_3309114279325780_4647173820898967567_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=e3f864&_nc_ohc=2fiRB6t2HN0AX9Jcfi2&_nc_ht=scontent.fvca2-1.fna&oh=00_AfBUlLkoUCLAYE1hYiu_Gf48ROXP3HmT8CF3il6cTTTFBQ&oe=638686BE" />
            <div></div>
          </div>
        </Col>
        <Col span={8}>
          <div style={{display: 'flex', flexDirection: 'column',height: 200}}>
            <Image style={{height: '70%', width: '100%'}} preview={false} src="https://scontent.fvca2-1.fna.fbcdn.net/v/t39.30808-6/305994841_3309114279325780_4647173820898967567_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=e3f864&_nc_ohc=2fiRB6t2HN0AX9Jcfi2&_nc_ht=scontent.fvca2-1.fna&oh=00_AfBUlLkoUCLAYE1hYiu_Gf48ROXP3HmT8CF3il6cTTTFBQ&oe=638686BE" />
            <div></div>
          </div>
        </Col>
        <Col span={8}>
          <div style={{display: 'flex', flexDirection: 'column',height: 200}}>
            <Image style={{height: '70%', width: '100%'}} preview={false} src="https://scontent.fvca2-1.fna.fbcdn.net/v/t39.30808-6/305994841_3309114279325780_4647173820898967567_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=e3f864&_nc_ohc=2fiRB6t2HN0AX9Jcfi2&_nc_ht=scontent.fvca2-1.fna&oh=00_AfBUlLkoUCLAYE1hYiu_Gf48ROXP3HmT8CF3il6cTTTFBQ&oe=638686BE" />
            <div></div>
          </div>
        </Col>
        <Col span={8}>
          <div style={{display: 'flex', flexDirection: 'column',height: 200}}>
            <Image style={{height: '70%', width: '100%'}} preview={false} src="https://scontent.fvca2-1.fna.fbcdn.net/v/t39.30808-6/305994841_3309114279325780_4647173820898967567_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=e3f864&_nc_ohc=2fiRB6t2HN0AX9Jcfi2&_nc_ht=scontent.fvca2-1.fna&oh=00_AfBUlLkoUCLAYE1hYiu_Gf48ROXP3HmT8CF3il6cTTTFBQ&oe=638686BE" />
            <div></div>
          </div>
        </Col>
      </Row>
    </div>
  );
}
