import { Row, Col, Avatar, Image, Button } from "antd";
import React, { useState, useEffect } from "react";
import Story from "../../components/Story";
import { AiOutlineVideoCameraAdd } from "react-icons/ai";
import { MdOutlineCollections, MdInsertEmoticon } from "react-icons/md";
import Post from "../../components/Post";
import AddPost from "../../components/AddPost";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../../services/firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth } from "../../services/firebase";

export default function Home() {
  const [dataPost, setDataPost] = useState([]);
  const [user] = useAuthState(auth);
  useEffect(() => {
    const fectPosts = async () => {
      const listPosts = [];
      const querySnapshot = await getDocs(collection(db, "Posts"));
      querySnapshot.forEach((doc) => {
        // doc.data() is never undefined for query doc snapshots
        // console.log(doc.id, " => ", doc.data());
        listPosts.push({ id: doc.id, ...doc.data() });
      });
      setDataPost(listPosts);
    };
    fectPosts();
  }, []);
  console.log(dataPost)
  return (
    <div
      style={{
        backgroundColor: "transparent",
        height: 1000,
        padding: "0 50px",
      }}
    >
      <Row>
        <Col
          span={24}
          style={{
            borderRadius: 8,
            overflow: "hidden",
            boxShadow:
              "rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 2px 6px 2px",
          }}
        >
          <Story />
        </Col>
      </Row>

      {user && (
        <Row style={{}}>
          <Col span={24}>
            <AddPost />
          </Col>
        </Row>
      )}

      <Row>
        <Col
          span={24}
          style={{
            borderRadius: 8,
            overflow: "hidden",
            boxShadow:
              "rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 2px 6px 2px",
          }}
        >
          {dataPost.map((item, index) => (
            <Post
              imgAuthor={item.imgAuthor}
              img={item.img}
              content={item.content}
              author={item.author}
              timeUp={item.timeUp.seconds}
              timeUpNano={item.timeUp.nanoseconds}
              key={index}
            />
          ))}
        </Col>
      </Row>
    </div>
  );
}
