import React from 'react'
import {Avatar, Image, Button } from "antd";
import { AiOutlineVideoCameraAdd } from "react-icons/ai";
import { MdOutlineCollections, MdInsertEmoticon } from "react-icons/md";

export default function AddPost() {
  return (
    <div>    <div style={{ display: "flex", alignItems: "center", marginBottom: 10 }}>
    <Avatar
      style={{ width: 40, height: 40, marginRight: 10 }}
      src={
        <Image
          src="https://joeschmoe.io/api/v1/random"
          style={{ width: 40 }}
          preview={false}
        />
      }
    />
    <Button
      className="btn-add-post"
      type="text"
      style={{
        backgroundColor: "#F0F2F5",
        flex: 1,
        borderRadius: 40,
        textAlign: "left",
        fontSize: 16,
        display: "flex",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <span style={{ opacity: 0.6 }}>
        Khoa ơi! bạn đang nghĩ gì thế?
      </span>
    </Button>
  </div>
  <div style={{ display: "flex", justifyContent: "space-between", borderTop: '1px solid #E4E6E9', paddingTop: 10 }}>
    <Button
      type="text"
      style={{
        fontSize: 16,
        display: "flex",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <AiOutlineVideoCameraAdd
        style={{ marginRight: 10, color: "red", fontSize: 25 }}
      />
      <span>Video trực tiếp</span>
    </Button>
    <Button
      type="text"
      style={{
        fontSize: 16,
        display: "flex",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <MdOutlineCollections
        style={{ marginRight: 10, color: "green", fontSize: 25 }}
      />
      <span>Ảnh/video</span>
    </Button>
    <Button
      type="text"
      style={{
        fontSize: 16,
        display: "flex",
        alignItems: "center",
        padding: "20px",
      }}
    >
      <MdInsertEmoticon
        style={{ marginRight: 10, color: "orange", fontSize: 25 }}
      />
      <span>Feeling</span>
    </Button>
  </div></div>
  )
}
